import React, { useState } from "react";
import { View, FlatList, Button } from "react-native";
import GoalInput from "./GoalInput";
import GoalList from "./GoalList";

export default function App() {
  const [goallist, setgoallist] = useState([]);

  const handlesubmit = (coursegoal) => {
    // console.log(coursegoal);
    setgoallist((currentgoals) => [
      ...currentgoals,
      { id: Math.random().toString(), value: coursegoal },
    ]);
  };
  const removehandler=(goalId)=>{
setgoallist((currentgoals)=>{
  console.log(goalId);
  currentgoals.filter((singlegoal)=>{
    singlegoal.id !==goalId
  })
})
  }
  return (
    <View style={{ padding: 50 }}>
      <GoalInput handlesubmit={handlesubmit} />

      <FlatList
        keyExtractor={(item, index) => item.id}
        data={goallist}
        renderItem={(itemdata) => <GoalList id={itemdata.item.id} ondelete={(id)=>{removehandler(id)}} title={itemdata.item.value} />}
      />
    </View>
  );
}
