import React, { useState } from "react";
import {
  Button,
  StyleSheet,
  Text,
  TextInput,
  View,
  ScrollView,
  FlatList,
} from "react-native";
import GoalInput from "./GoalInput";
import GoalList from "./GoalList";

export default function App() {
  const handlechange = (entertext) => {
    setcoursegoal(entertext);
  };
  const handlesubmit = () => {
    setgoallist((currentgoals) => [...currentgoals,{id:Math.random().toString(),value:coursegoal}]);
  };
  return (
    <View style={{ padding: 50 }}>
      <GoalInput handlechange={handlechange} handlesubmit={handlesubmit}/>
      {/* <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <TextInput
          placeholder="Course Goal"
          value={coursegoal}
          onChangeText={handlechange}
          style={{
            width: "80%",
            borderBottomColor: "black",
            borderBottomWidth: 1,
            padding: 10,
          }}
        />
        <Button title="ADD" onPress={handlesubmit} />
      </View> */}
      <FlatList 
      keyExtractor={(item,index)=>item.id}
      data={goallist}
      renderItem={(itemdata)=><GoalList title={itemdata.item.value}/>
      }
      />
      {/* <ScrollView>
     {goallist.map((a,i)=>(
        <View key={i} style={styles.list}>
         <Text> {a}</Text>
          </View>
      ))}
       </ScrollView> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // backgroundColor: '#fff',
    // alignItems: 'center',
    // justifyContent: 'center',
  },
  list: {
    padding: 10,
    marginVertical: 10,
    borderWidth: 1,
  },
});
